sequential.echo:
Manifest-Version: 1.0
Bundle-Name: Test Localization for @{Bundle}
Bundle-SymbolicName: @{Bundle}.nl_re;singleton=true
Bundle-Version: ${.bundle.version}
Bundle-Vendor: Eclipse.org - Sapphire
Fragment-Host: @{Bundle}

if.scriptcondition:

var dateParts = project.getProperty( ".dfe.metadata.LastUsed" ).split( "-" );

if( dateParts.length == 3 )
{
    var lastUsed = new Date( dateParts[ 0 ], dateParts[ 1 ] - 1, dateParts[ 2 ] );
    var today = new Date();

    if( today.getTime() - lastUsed.getTime() < ( 30 * 24 * 60 * 60 * 1000 ) )
    {
        self.value = false;
    }
    else
    {
        self.value = true;
  }
}
else
{
    self.value = false;
}
